mapper.select:
SELECT i.item_id as itemId, i.name, i.price, i.main_url FROM item i INNER JOIN item_category ic ON i.item_id = ic.item_id INNER JOIN category c ON ic.category_id = c.category_id
c.name = #{category}
ORDER BY i.created LIMIT #{limit} OFFSET #{offset}

SELECT i.item_id, i.price, i.name, id.item_detail_id FROM item i INNER JOIN item_detail id ON i.item_id = id.item_id WHERE i.item_id = #{id} AND id.color = #{color} AND
id.size IS NULL
id.size = #{size}

select id.item_detail_id
from item_detail id
where id.item_id = #{itemId} and id.size = #{size} and id.color = #{color}

SELECT item_detail_id as itemDetailId, stock_quantity FROM item_detail WHERE item_detail_id IN
#{id}
FOR UPDATE

mapper.update:
UPDATE item_detail
SET stock_quantity = stock_quantity - #{quantity}
WHERE item_id = #{id}
AND color = #{color}

    AND size IS NULL


    AND size = #{size}

update item_detail
set stock_quantity = stock_quantity - #{quantity}
WHERE item_detail_id = #{itemDetailId}
  AND stock_quantity >= #{quantity};

update item_detail
set stock_quantity = stock_quantity + #{quantity}
WHERE item_detail_id = #{itemDetailId}
