mapper.update:
UPDATE question
SET
    title = #{title},
    content = #{content},
    img_url = #{imgUrl}
WHERE question_id = #{questionId}

UPDATE question_answer
SET
    content = #{content},
    title = #{title}
WHERE
    question_answer_id = #{questionAnswerId} and admin_id = #{adminId}

mapper.select:
select question_id
from question_answer
where question_answer_id = #{questionAnswerId}
